mapper.insert:
insert into member(
	member_id, member_pwd, member_name, zipcode,
	addr1, addr2, tel1, tel2, tel3,
	phone1, phone2, phone3, email, email_check
) values (
	#{member_id}, #{member_pwd}, #{member_name}, #{zipcode},
	#{addr1}, #{addr2}, #{tel1}, #{tel2}, #{tel3},
	#{phone1}, #{phone2}, #{phone3}, #{email}, #{email_check}
)

mapper.update:
update member set 
	member_pwd = #{member_pwd}, 
	rank_num = #{rank_num}
