mapper.insert:
insert into PRODUCT_CATEGORY (
	product_category_num,
	product_category_name
) values (
	SEQ_PRODUCT_CATEGORY_NUM.nextval,
	#{product_category_name}
)

begin
	insert into product_category_link(
	    PRODUCT_NUMBER,
	    PRODUCT_CATEGORY_NUM
	) values (
	    #{product_number},
	    #{cateCode1}
	);

	insert into product_category_link(
	    PRODUCT_NUMBER,
	    PRODUCT_CATEGORY_NUM
	) values (
	    #{product_number},
	    #{cateCode2}
	);


		insert into product_category_link(
		    PRODUCT_NUMBER,
		    PRODUCT_CATEGORY_NUM
		) values (
		    #{product_number},
		    #{cateCode3}
		);



	insert into product_category_link(
	    PRODUCT_NUMBER,
	    PRODUCT_CATEGORY_NUM
	) values (
	    #{product_number},
	    #{brandCategory}
	);
end;

mapper.select:
select * from product where cateCode1 = #{cateCode1} 

	and cateCode2 = #{cateCode2}


	and cateCode3 = #{cateCode3}


	and brandCategory = #{brandCategory}
